 	function getStorageVariable(varName) {
		if(typeof(Storage) !== "undefined") {
			if (sessionStorage.ldControllerSettings != null) {
				ldControllerSettingsContainer = JSON.parse(sessionStorage.ldControllerSettings);

				if ("#[ w DatasourceClassName ]#" in ldControllerSettingsContainer) {
					ldControllerSettings = JSON.parse(ldControllerSettingsContainer["#[ w DatasourceClassName ]#"]);
					if (varName in ldControllerSettings) {
						return ldControllerSettings[varName];
					}
					else { 
						return null;
					}
				}
				else {
					return null;	
				}
			}
			else {
				return null;	
			}
		}
		else {
			return null;	
		}			
	}

	function setStorageVariable(varName, varValue) {
		if(typeof(Storage) !== "undefined"){

			ldControllerSettings = new Object();
			ldControllerSettingsContainer  = new Object();

			if (sessionStorage.ldControllerSettings != null &&  sessionStorage.ldControllerSettings != "") {
				ldControllerSettingsContainer = JSON.parse(sessionStorage.ldControllerSettings);

				if ("#[ w DatasourceClassName ]#" in ldControllerSettingsContainer) {
					ldControllerSettings = JSON.parse(ldControllerSettingsContainer["#[ w DatasourceClassName ]#"]);
				}
			}
			else {
				ldControllerSettingsContainer["#[ w DatasourceClassName ]#"] = new Object();
			}

			ldControllerSettings[varName] = varValue;
			ldControllerSettingsContainer["#[ w DatasourceClassName ]#"] = JSON.stringify(ldControllerSettings);
			sessionStorage.ldControllerSettings = JSON.stringify(ldControllerSettingsContainer);
		}

	}


