        IF ($EXTRACT(prop) '= "%") {
	        Do %code.WriteLine(" Do array.SetAt(.."_prop_","""_prop_""")")
        }
    }

    // Return an %ArrayOfDataTypes representation of the object
    Do %code.WriteLine(" Quit array")
